import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ProfilePage = ({ user }) => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [userData, setUserData] = useState(null);
  const [purchases, setPurchases] = useState([]);

  useEffect(() => {
    if (!user) return;

    const fetchProfileAndPurchases = async () => {
      try {
        const config = {
          headers: {
            Authorization: `Bearer ${user.token}`,
          },
        };

        const [profileRes, purchasesRes] = await Promise.all([
          axios.get('http://localhost:5000/api/users/profile', config),
          axios.get('http://localhost:5000/api/purchases/mine', config),
        ]);

        setUserData(profileRes.data);
        setPurchases(purchasesRes.data);
        setLoading(false);
      } catch (err) {
        setError('Došlo je do greške prilikom dohvaćanja podataka.');
        setLoading(false);
      }
    };

    fetchProfileAndPurchases();
  }, [user]);

  if (!user) {
    return (
      <div className="profile">
        <h1>Profil</h1>
        <p>Morate biti prijavljeni da biste vidjeli svoj profil.</p>
      </div>
    );
  }

  if (loading) return <div>Učitavanje...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="profile">
      <h1>Profil</h1>

      <div>
        <h2>Korisnički podaci</h2>
        <p><strong>Ime:</strong> {userData.name || 'Nepoznato'}</p>
        <p><strong>Email:</strong> {userData.email}</p>
        {userData.createdAt && (
          <p><strong>Registriran:</strong> {new Date(userData.createdAt).toLocaleDateString()}</p>
        )}
      </div>

      <div>
        <h2>Kupljeni filmovi</h2>
        {purchases.length === 0 ? (
          <p>Još niste kupili nijedan film.</p>
        ) : (
          <ul>
            {purchases.map((purchase) => (
              <li key={purchase._id}>
                {purchase.movie?.title || 'Nepoznat naslov'} ({new Date(purchase.purchaseDate).toLocaleDateString()})
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default ProfilePage;
