import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ProfilePage = ({ user, addToCart }) => {
  const [favoriteMovies, setFavoriteMovies] = useState([]);
  const [watchHistory, setWatchHistory] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!user) return;

    const fetchUserMovies = async () => {
      try {
        // Fetch user profile
        const config = {
          headers: {
            Authorization: `Bearer ${user.token}`,
          },
        };

        const { data } = await axios.get(
          'http://localhost:5000/api/users/profile',
          config
        );

        // Fetch favorite movies
        if (data.favoriteMovies && data.favoriteMovies.length > 0) {
          const favoriteMoviesData = await Promise.all(
            data.favoriteMovies.map(async (movieId) => {
              const { data } = await axios.get(`http://localhost:5000/api/movies/${movieId}`);
              return data;
            })
          );
          setFavoriteMovies(favoriteMoviesData);
        }

        // Fetch watch history
        if (data.watchHistory && data.watchHistory.length > 0) {
          const watchHistoryData = await Promise.all(
            data.watchHistory.map(async (movieId) => {
              const { data } = await axios.get(`http://localhost:5000/api/movies/${movieId}`);
              return data;
            })
          );
          setWatchHistory(watchHistoryData);
        }

        setLoading(false);
      } catch (error) {
        setError('Došlo je do greške prilikom dohvaćanja podataka o korisniku.');
        setLoading(false);
      }
    };

    fetchUserMovies();
  }, [user]);

  if (!user) {
    return (
      <div>
        <h1>Profil</h1>
        <p>Morate biti prijavljeni da biste vidjeli svoj profil.</p>
      </div>
    );
  }

  if (loading) return <div>Učitavanje...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="profile">
      <h1>Profil</h1>

      <div>
        <h2>Korisnički podaci</h2>
        <p><strong>Ime:</strong> {user.name}</p>
        <p><strong>Email:</strong> {user.email}</p>
      </div>
    </div>
  );
};

export default ProfilePage;
